mapper.select:
SELECT goods_title , goods_name , goods_color , goods_weight ,goods_content ,goods_type , activity_end_date ,goods_score ,goods_price , goods_remark ,goods_sort from yxjj_goods where is_delete = 2
and id = #{goodsID}
and goods_type = #{goodsType}
order by goods_sort ASC, create_date DESC LIMIT ${pageNumber},${pageSize}

mapper.update:
update yxjj_goods set
 is_delete = 1,
`delete_uid`=#{deleteUID},
`delete_date`=#{deleteDate}
where id=#{id}
